import React from "react";
import ContainerWrapper from "./ContainerWrapper";
import logo from "../../logo.svg";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faCalendar, faHome, faUniversity } from "@fortawesome/free-solid-svg-icons";
import { Button } from "reactstrap";
import { Link, useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { toggleSidebar } from "../../redux/actions/globalActions";

const Container = ({ children }) => {
  const { page } = useParams();
  const isShow = useSelector((state) => state.global.isSidebarShow);

  return (
    <ContainerWrapper>
      <div className={`sidebar ${(isShow && "show") || ""}`}>
        <p className="fw-bold fs-3 mb-4">M{isShow && <span>enu</span>}</p>
        <ul className="menu">
          <li>
            <Link to="/" className={`${(page == "app" && "active") || ""}`}>
              <FontAwesomeIcon icon={faHome} /> {isShow && "App"}
            </Link>
          </li>
          <li>
            <Link
              to="/dashboard/users"
              className={`${(page == "e-commerce" && "active") || ""}`}
            >
              <FontAwesomeIcon icon={faHome} /> {isShow && "E-Commerce"}
            </Link>
          </li>
          <li>
            <Link
              to="/dashboard/todos"
              className={`${(page == "analytics" && "active") || ""}`}
            >
              <FontAwesomeIcon icon={faHome} /> {isShow && "Analytics"}
            </Link>
          </li>
          <li>
            <Link
              to="/dashboard/albums"
              className={`${(page == "banking" && "active") || ""}`}
            >
              <FontAwesomeIcon icon={faUniversity} /> {isShow && "Banking"}
            </Link>
          </li>
          <li>
            <Link
              to="/dashboard/photos"
              className={`${(page == "booking" && "active") || ""}`}
            >
              <FontAwesomeIcon icon={faCalend} /> {isShow && "Booking"}
            </Link>
          </li>
          <li>
            <Link
              to="/dashboard/posts"
              className={`${(page == "users" && "active") || ""}`}
            >
              <FontAwesomeIcon icon={faHome} /> {isShow && "User"}
            </Link>
          </li>
        </ul>
      </div>
      <div className="rightside">
        <header className="px-2 py-3 shadow">
          <Button color="light" onClick={toggleSidebar}>
            <FontAwesomeIcon icon={faBars} />
          </Button>
          <img src={logo} className="logo" alt="" />
        </header>
        <div className="content">{children}</div>
      </div>
    </ContainerWrapper>
  );
};

export default Container;
